INSERT INTO `wp_posts` VALUES ('1522', '1', '2013-12-20 19:37:27', '2013-12-20 19:37:27', '<h3>Master Slider</h3>\n<span style=\"color: #444444;\">Master Slider is a premium </span><strong style=\"color: #444444;\">image and content slider</strong><span style=\"color: #444444;\"> with </span><strong style=\"color: #444444;\">super smooth hardware accelerated transitions</strong><span style=\"color: #444444;\">. It supports </span><strong style=\"color: #444444;\">touch navigation</strong><span style=\"color: #444444;\"> with </span><strong style=\"color: #444444;\">pure swipe gesture</strong><span style=\"color: #444444;\"> that you have never experienced before. It’s a truly </span><strong style=\"color: #444444;\">responsive</strong><span style=\"color: #444444;\"> and </span><strong style=\"color: #444444;\">device friendly</strong><span style=\"color: #444444;\"> slider which works perfect in all major devices.</span>\n\n<a href=\"http://demo.goodlayers.com/versatile/wp-content/uploads/2013/12/feature-master-slider.jpg\"><img class=\"alignnone size-full wp-image-2681\" src=\"http://demo.goodlayers.com/versatile/wp-content/uploads/2013/12/feature-master-slider.jpg\" alt=\"feature-master-slider\" width=\"620\" height=\"600\" /></a>', 'Master Slider', '', 'publish', 'closed', 'open', '', 'layer-slider-2', '', '', '2013-12-20 19:37:27', '2013-12-20 19:37:27', '', '0', 'http://themes.goodlayers2.com/flawless/?page_id=1522', '0', 'page', '', '0');
INSERT INTO `wp_posts` VALUES ('1525', '1', '2013-12-20 19:40:34', '2013-12-20 19:40:34', '<h3>Optimized Code & SEO</h3>\nWe reconstructed about how to use title. We use heading tag(H1,H2,H3,…) for title which is good practice for search engine site. Main contents are moved above sidebars(including both sidebar layout), now search engine will easily fine your main contents first. Also, ‘alt’ tag for images is supported. This is also help search engine to analyst your site easily. \n\n<a href=\"http://demo.goodlayers.com/flawless/wp-content/uploads/2013/12/feature-15-1.jpg\"><img src=\"http://demo.goodlayers.com/flawless/wp-content/uploads/2013/12/feature-15-1.jpg\" alt=\"feature-15-1\" width=\"620\" height=\"600\" class=\"alignnone size-full wp-image-1526\" /></a>', 'Optimized Code & SEO', '', 'publish', 'closed', 'open', '', 'optimized-code-seo', '', '', '2013-12-20 19:40:34', '2013-12-20 19:40:34', '', '0', 'http://themes.goodlayers2.com/flawless/?page_id=1525', '0', 'page', '', '0');
